mapper.select:
SELECT COUNT(1) FROM `t_client`client
client.`deleted` != 1
AND client.`name` LIKE "%"#{searchDto.name}"%"
AND client.`idcard_num` LIKE "%"#{searchDto.idCardNum}"%"
AND client.`tel` LIKE "%"#{searchDto.tel}"%"
AND client.`dic_type` = #{searchDto.dicType}
AND client.`dic_mci_type` = #{searchDto.dicMCIType}
AND client.`all_cost` >= #{searchDto.allCost}
AND client.`created_time` >= #{searchDto.startDate}
AND client.`created_time` <= #{searchDto.endDate}

SELECT COUNT(1) FROM `t_client`client
`deleted` != 1 AND `is_member` != 0
AND client.`name` LIKE "%"#{searchDto.name}"%"
AND client.`idcard_num` LIKE "%"#{searchDto.idCardNum}"%"
AND client.`tel` LIKE "%"#{searchDto.tel}"%"
AND client.`member_group_id` = #{searchDto.memberGroupId}
AND client.`member_status` = #{searchDto.memberStatus}
AND client.`dic_type` = #{searchDto.level}
AND client.`is_fixed` = #{searchDto.isFixed}

SELECT COUNT(1)
FROM `t_client`
WHERE `deleted`  != 1 AND `is_member` != 0 AND `member_group_id` = #{id}

SELECT * FROM `t_client`client
client.`deleted` != 1 AND client.`is_member` != 0
AND client.`name` LIKE "%"#{searchDto.name}"%"
AND client.`idcard_num` LIKE "%"#{searchDto.idCardNum}"%"
AND client.`tel` LIKE "%"#{searchDto.tel}"%"
AND client.`member_group_id` = #{searchDto.memberGroupId}
AND client.`member_status` = #{searchDto.memberStatus}
AND client.`dic_type` = #{searchDto.level}
AND client.`is_fixed` = #{searchDto.isFixed}
ORDER BY client.`created_time` DESC
LIMIT #{searchDto.offset}, #{searchDto.pageSize}

SELECT client.`id` AS id, client.`name` AS name, client.`gender` AS gender, client.`idcard_num` AS idCardNum, client.`tel` AS tel, client.`diagnose_type` AS diagnoseType, client.`dic_Type` AS dicType, client.`dic_mci_type` AS dicMCIType, client.`all_cost` AS allCost, client.`created_party_id` AS createdPartyId, client.`created_time` AS createdTime, client.`last_modified_time` AS lastModifiedTime FROM `t_client`client
client.`deleted` != 1
AND client.`name` LIKE "%"#{searchDto.name}"%"
AND client.`idcard_num` LIKE "%"#{searchDto.idCardNum}"%"
AND client.`tel` LIKE "%"#{searchDto.tel}"%"
AND client.`dic_type` = #{searchDto.dicType}
AND client.`dic_mci_type` = #{searchDto.dicMCIType}
AND client.`all_cost` >= #{searchDto.allCost}
AND client.`created_time` >= #{searchDto.startDate}
AND client.`created_time` <= #{searchDto.endDate}
ORDER BY client.`last_modified_time` DESC
LIMIT #{searchDto.offset}, #{searchDto.pageSize}

SELECT client.`id` AS id, client.`name` AS name, client.`gender` AS gender, client.`idcard_num` AS idCardNum, client.`tel` AS tel, client.`diagnose_type` AS diagnoseType, client.`dic_Type` AS dicType, dictionary1.`name` AS dicTypeName, client.`dic_mci_type` AS dicMCIType, dictionary2.`name` AS dicMCITypeName, client.`all_cost` AS allCost, client.`created_party_id` AS createdPartyId, client.`created_time` AS createdTime, client.`last_modified_time` AS lastModifiedTime FROM `t_client`client LEFT JOIN `t_dictionary`dictionary1 ON dictionary1.`id` = client.`dic_Type` LEFT JOIN `t_dictionary`dictionary2 ON dictionary2.`id` = client.`dic_mci_type`
client.`deleted` != 1
AND client.`is_member` != 0
AND client.`name` LIKE "%"#{searchDto.name}"%"
AND client.`idcard_num` LIKE "%"#{searchDto.idCardNum}"%"
AND client.`tel` LIKE "%"#{searchDto.tel}"%"
AND client.`member_group_id` = #{searchDto.memberGroupId}
AND client.`dic_type` = #{searchDto.dicType}
AND client.`dic_mci_type` = #{searchDto.dicMCIType}
AND client.`all_cost` >= #{searchDto.allCost}
ORDER BY client.`last_modified_time` DESC LIMIT 20

SELECT client.`id` AS id, client.`name` AS name, client.`gender` AS gender, client.`idcard_num` AS idCardNum, client.`tel` AS tel, client.`diagnose_type` AS diagnoseType, client.`dic_Type` AS dicType, dictionary1.`name` AS dicTypeName, client.`dic_mci_type` AS dicMCIType, dictionary2.`name` AS dicMCITypeName, client.`all_cost` AS allCost, client.`created_party_id` AS createdPartyId, client.`created_time` AS createdTime, client.`last_modified_time` AS lastModifiedTime FROM `t_client`client LEFT JOIN `t_dictionary`dictionary1 ON dictionary1.`id` = client.`dic_Type` LEFT JOIN `t_dictionary`dictionary2 ON dictionary2.`id` = client.`dic_mci_type`
client.`deleted` != 1
AND client.`is_member` != 0
AND client.`name` LIKE "%"#{searchDto.name}"%"
AND client.`idcard_num` LIKE "%"#{searchDto.idCardNum}"%"
AND client.`tel` LIKE "%"#{searchDto.tel}"%"
AND client.`dic_type` = #{searchDto.dicType}
AND client.`dic_mci_type` = #{searchDto.dicMCIType}
AND client.`all_cost` >= #{searchDto.allCost}
AND client.`member_group_id` = #{searchDto.memberGroupId}
ORDER BY client.`last_modified_time` DESC LIMIT 20
