mapper.select:
SELECT COUNT(1) FROM `t_quest`quest , `t_client`client
client.`deleted` != 1 AND client.`is_member` != 0 AND client.`id` = quest.`client_id`
AND client.`name` LIKE "%"#{searchDto.name}"%"
AND client.`idcard_num` LIKE "%"#{searchDto.idCardNum}"%"
AND client.`tel` LIKE "%"#{searchDto.tel}"%"
AND quest.`next_quest_time` >= #{searchDto.startDate}
AND quest.`next_quest_time` <= #{searchDto.endDate}
AND quest.`member_group_id` = #{searchDto.memberGroupId}

SELECT quest.`id` AS id, quest.`client_id` AS clientId, client.`name` AS clientName, client.`idcard_num` AS idCardNum, client.`tel` AS tel, client.`all_cost` AS allCost, client.`is_member` AS isMember, client.`member_group_id` AS memberGroupId, memberGroup.`name` AS memberGroupName, client.`quest_count` AS questCount, quest.`quest_time` AS questTime, quest.`quest_types` AS questTypes, quest.`is_remindahead` AS isRemindahead, quest.`remindahead_days` AS remindaheadDay, max(quest.`next_quest_time`) AS nextQuestTime, quest.`comment` AS comment, quest.`created_party_id` AS createdPartyId, quest.`created_time` AS createdTime, quest.`last_modified_time` AS lastModifiedTime FROM `t_client`client,`t_quest`quest,`t_member_group`memberGroup
client.`deleted` != 1 AND client.`is_member` != 0 AND client.`member_group_id` = memberGroup.`id` AND quest.`client_id` = client.`id`
AND client.`name` LIKE "%"#{searchDto.name}"%"
AND client.`idcard_num` LIKE "%"#{searchDto.idCardNum}"%"
AND client.`tel` LIKE "%"#{searchDto.tel}"%"
AND quest.`next_quest_time` >= #{searchDto.startDate}
AND quest.`next_quest_time` <= #{searchDto.endDate}
AND quest.`member_group_id` = #{searchDto.memberGroupId}
AND client.`quest_count` = #{searchDto.questCount}
GROUP BY quest.`client_id` ORDER BY nextQuestTime ASC
LIMIT #{searchDto.offset}, #{searchDto.pageSize}

SELECT client.`id` AS id, client.`name` AS name, client.`gender` AS gender, client.`idcard_num` AS idCardNum, client.`tel` AS tel, client.`diagnose_type` AS diagnoseType, client.`dic_Type` AS dicType, dictionary1.`name` AS dicTypeName, client.`dic_mci_type` AS dicMCIType, dictionary2.`name` AS dicMCITypeName, client.`all_cost` AS allCost FROM `t_client`client LEFT JOIN `t_dictionary`dictionary1 ON dictionary1.`id` = client.`dic_Type` LEFT JOIN `t_dictionary`dictionary2 ON dictionary2.`id` = client.`dic_mci_type`
client.`deleted` != 1 AND client.`is_member` !=0
AND client.`name` LIKE "%"#{searchDto.name}"%"
AND client.`idcard_num` LIKE "%"#{searchDto.idCardNum}"%"
AND client.`tel` LIKE "%"#{searchDto.tel}"%"
AND client.`dic_type` = #{searchDto.dicType}
AND client.`dic_mci_type` = #{searchDto.dicMCIType}
AND client.`all_cost` >= #{searchDto.costGt}
ORDER BY client.`last_modified_time` DESC LIMIT 20

SELECT count(1) FROM ( SELECT quest.`id` AS id FROM `t_client`client,`t_quest`quest,`t_member_group`memberGroup
client.`deleted` != 1 AND client.`is_member` != 0 AND client.`member_group_id` = memberGroup.`id` AND quest.`client_id` = client.`id`
AND client.`name` LIKE "%"#{searchDto.name}"%"
AND client.`idcard_num` LIKE "%"#{searchDto.idCardNum}"%"
AND client.`tel` LIKE "%"#{searchDto.tel}"%"
AND quest.`next_quest_time` >= #{searchDto.startDate}
AND quest.`next_quest_time` <= #{searchDto.endDate}
AND quest.`member_group_id` = #{searchDto.memberGroupId}
AND client.`quest_count` = #{searchDto.questCount}
GROUP BY quest.`client_id`) AS t
